#include "get_next_line.h"
#include "gnl_test.h"

int	gnl_test_12(void)
{
	char	*str;
	int		fd;
	int		ret;

	fd = open("./files/06.txt", O_RDONLY);
	str = get_next_line(fd);
	close(fd);
	ret = strcmp(str, "");
	if (ret == 0)
		return (TEST_SUCCESS);
	return (TEST_FAILLURE);
}
